package com.robo4j.socket.http.util;

import com.robo4j.RoboBuilder;
import com.robo4j.RoboContext;
import com.robo4j.RoboReference;
import com.robo4j.util.SystemUtil;

import java.io.InputStream;
import java.util.concurrent.TimeUnit;

/**
 * Helper test utility
 *
 * @author Marcus Hirt (@hirt)
 * @author Miro Wengner (@miragemiko)
 */
public class HttpUnitHelper {



	/**
	 * example of image encoded
	 *
	 * @return encoded Sting image string
	 */
	public static String getExampleCamera() {
		return "";
	}

	public void testHttpRealServerTestWithClient() throws Exception {
		InputStream inputStream = Thread.currentThread().getContextClassLoader()
				.getResourceAsStream("robo_image_server.xml");
		RoboBuilder builder = new RoboBuilder().add(inputStream);
		RoboContext roboSystem = builder.build();
		roboSystem.start();
		System.out.println("RoboSystem after start:");
		System.out.println(SystemUtil.printStateReport(roboSystem));
		System.in.read();
	}

	public void testHttpImageClientServer() throws Exception {

		InputStream isClient = Thread.currentThread().getContextClassLoader()
				.getResourceAsStream("robo_image_client.xml");
		RoboBuilder builder = new RoboBuilder().add(isClient);
		RoboContext roboSystemClient = builder.build();
		roboSystemClient.start();
		System.out.println("RoboSystem Client after start:");
		System.out.println(SystemUtil.printStateReport(roboSystemClient));
		RoboReference<Boolean> imageController = roboSystemClient.getReference("imageController");
		roboSystemClient.getScheduler().schedule(imageController, true, 0, 5, TimeUnit.SECONDS);

		InputStream isServer = Thread.currentThread().getContextClassLoader()
				.getResourceAsStream("robo_image_server.xml");
		builder = new RoboBuilder().add(isServer);
		RoboContext roboSystemServer = builder.build();
		roboSystemServer.start();
		System.out.println("RoboSystem Server after start:");
		System.out.println(SystemUtil.printStateReport(roboSystemServer));
		System.in.read();
	}

}
